import React, { Component } from 'react'
import { Link } from 'react-router-dom'


import beerService from '../../../services/beer.service'


class BeerDetails extends Component {
    constructor() {
        super()
        this.state = {
            beer: []
        }
    }

    componentDidMount() {
        const baseUrl = 'https://ih-beers-api2.herokuapp.com/beers'
        fetch(baseUrl)
            .then((response) => response.json())
            .then((data) => this.setState({ beer: data }))
    }

    render() {
        console.log(this.state.tagline)
        return (
            <>
                <img alt={this.state.name} src={this.state.image_url} />
                <p>{this.state.name}</p>
                <hr />
                <p>{this.state.tagline}</p>
                <p>{this.state.first_brewed}</p>
                <p>{this.state.attenuation_level}</p>
                <p>{this.state.description}</p>
                <p>{this.state.contributed_by}</p>

                <hr />
                <Link to="/coasters" className="btn btn-dark btn-sm">Volver al Ã­ndice</Link>
            </>
        )
    }
}

export default BeerDetails
