import React, { Component } from 'react'
import countries from "../../countries.json";

class CountryDetails extends Component {
    constructor(props) {
        super()
        const { id } = props.match.params
    }

    render() {
        console.log(this.id)
        const country = countries.find(elm => elm.cca3)
        return (
            <>
                <p>{country.name.common}</p>
                <p>{country.capital}</p>
                <p>{country.area}</p>
            </>
        )
    }
}

