import React, { Component } from 'react'
import countries from "../../countries.json";

class CountryDetails extends Component {
    constructor() {
        super()
        this.state = {
            countries: countries
        }
    }
    country = (id) => countries.filter(elm => elm.acc3 === id)

    render() {
        const { id } = this.props.match.params
        country = (id) => countries.filter(elm => elm.acc3 === id)
        return (
            <>
                <p>{country.capital}</p>
                <p>{country.area}</p>
            </>
        )
    }
}
