import React, { Component } from 'react'

class CountryDetails extends Component {
    render() {
        const { id } = this.prop.match.params
        let country = countries.map(elm => elm.cca3 === id)
        return (
            <>
                <p>{country.capital}</p>
                <p>{country.area}</p>
                <p>{country.borders}</p>
            </>
        )
    }
}

export default CountryDetails
